mapper.select:
select t2.id,t2.productTypeName,count( t1.productName) as productNumber from t_productType t2 left join t_product t1 on t1.productTypeId=t2.id
where t1.customerOpenId=#{customerOpenId}
GROUP BY t2.id

select t2.id,t2.productTypeName,count(DISTINCT t1.productName) as productNumber from t_productType t2 left join t_product t1 on t1.productTypeId=t2.id
where t1.customerOpenId=#{customerOpenId}
GROUP BY t2.id
